import React from "react";

import "./Header.hero.css";
import "../../App.css";

import hero from "../../pic/png/hero.png";
import { Typewriter, useTypewriter } from "react-simple-typewriter";

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faFacebook,
  faGithub,
  faLinkedinIn,
  faInstagram,
} from "@fortawesome/free-brands-svg-icons";

const facebook = <FontAwesomeIcon icon={faFacebook} />;
const github = <FontAwesomeIcon icon={faGithub} />;
const linkedin = <FontAwesomeIcon icon={faLinkedinIn} />;
const insta = <FontAwesomeIcon icon={faInstagram} />;

const HeaderHero = () => {
  const { text } = useTypewriter({
    words: [" Developer.", " Learner.", " Coder.", " Self Believer."],
    loop: 0,
    cursor,
    typeSpeed: 70,
    deleteSpeed: 50,
    delaySpeed: 1000,
  });

  return (
    <>
      <section className="hero" id="home">
        <div className="container f_flex top hero_container">
          <div className="left top">
            <h3>Welcome to my world...</h3>
            <h1>
              Hi, I'm <span>Samrat Alam</span>
            </h1>
            <h2>
              a{" "}
              <span>
                {text}
                {/* Self Believer | Developer | <br /> Coder | Learner.
              </span>
              <span>
                <Typewriter
                  words={[""]}
                  loop={5}
                  cursor
                  cursorStyle="|"
                  typeSpeed={70}
                  deleteSpeed={50}
                  delaySpeed={1000}
                /> */}
              </span>
            </h2>
            <p>Visit My Portfolio And Keep Your Feedback..</p>
            <div className="hero_btn d_flex">
              <div className="col_1">
                <h4>Find With Me:</h4>
                <button className="btn_shadow">
                  <a
                    href="https://www.facebook.com/profile.php?id=100003190767005"
                    target={"_blank"}
                    rel="noreferrer"
                  >
                    {facebook}
                  </a>
                </button>
                <button className="btn_shadow">
                  <a
                    href="https://www.linkedin.com/in/samrat-alam-8392b5167/"
                    target={"_blank"}
                    rel="noreferrer"
                  >
                    {linkedin}
                  </a>
                </button>
                <button className="btn_shadow">
                  <a
                    href="https://github.com/samratalamshanto"
                    target={"_blank"}
                    rel="noreferrer"
                  >
                    {github}
                  </a>
                </button>
                {/* <button className="btn_shadow">
                  <a href="https://www.instagram.com/samratshanto/?hl=en">
                    {" "}
                    <i
                      class="fab fa-researchgate"
                      style={{ fontSize: "17px" }}
                    ></i>
                  </a>
                </button> */}
                <button className="btn_shadow">
                  <a
                    target={"_blank"}
                    rel="noreferrer"
                    href="https://www.instagram.com/samratshanto/?hl=en"
                  >
                    {" "}
                    {insta}
                  </a>
                </button>
              </div>
            </div>
          </div>
          <div className="right top">
            <div className="right_img">
              <img src={hero} alt="hero" />
            </div>
          </div>
        </div>
      </section>
    </>
  );
};

export default HeaderHero;
